import { Component, ViewChild } from '@angular/core';
import { Smestaj } from './smestaj-lista/smestaj.model';
import { SmestajListaComponent } from './smestaj-lista/smestaj-lista.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  @ViewChild(SmestajListaComponent, { static: false }) smestajListaComponent!: SmestajListaComponent;

  title = 'Angular-Hotel';
  smestaj: Smestaj = {
    naziv: '',
    brojKreveta: 0,
    cenaPoNoci: 0
  };

  dodajSobu() {
    if (this.smestajListaComponent) {
      this.smestajListaComponent.smestaji.push(this.smestaj);

      this.smestaj = {
        naziv: '',
        brojKreveta: 0,
        cenaPoNoci: 0
      };
    } else {
      console.error('Nemam referencu na SmestajListaComponent!');
    }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.css">
  <script src="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.js"></script>
  <title>Hotel</title>
</head>
<body>
<div class="hotel-container">
  <h1 class="hotel-header">Hotels</h1>

  <app-smestaj-lista></app-smestaj-lista>

  <div class="add-room-section">
    <h3 class="section-title">Add New Room</h3>
    <div class="room-form">
      <div class="form-field">
        <label>Name</label>
        <input type="text" placeholder="Enter room name">
      </div>
      <div class="form-field">
        <label>Number of Beds</label>
        <input type="number" placeholder="Enter number of beds">
      </div>
      <div class="form-field">
        <label>Price per Night</label>
        <input type="number" placeholder="Enter price per night">
      </div>
      <button class="submit-button">Add Room</button>
    </div>
  </div>
</div>
</body>
</html>
