import { Component, ViewChild } from '@angular/core';
import { Smestaj } from './smestaj-lista/smestaj.model';
import { SmestajListaComponent } from './smestaj-lista/smestaj-lista.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  @ViewChild(SmestajListaComponent, { static: false }) smestajListaComponent!: SmestajListaComponent;

  title = 'Angular-Hotel';
  smestaj: Smestaj = {
    naziv: '',
    brojKreveta: 0,
    cenaPoNoci: 0
  };

  dodajSobu() {
    if (this.smestajListaComponent) {
      this.smestajListaComponent.smestaji.push(this.smestaj);

      this.smestaj = {
        naziv: '',
        brojKreveta: 0,
        cenaPoNoci: 0
      };
    } else {
      console.error('Nemam referencu na SmestajListaComponent!');
    }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Hotel</title>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.css">
  <script src="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.js"></script>
</head>
<body>
<div class="ui container">
  <h1 class="ui teal center aligned header">Hotels</h1>

  <app-smestaj-lista></app-smestaj-lista>

  <div class="ui segment">
    <h3 class="ui dividing header">Dodavanje Nove Sobe</h3>
    <div class="ui form">
      <div class="field">
        <label>Naziv</label>
        <input type="text" [(ngModel)]="smestaj.naziv" placeholder="Unesite naziv sobe" required="true">
      </div>
      <div class="field">
        <label>Broj Kreveta</label>
        <input type="number" [(ngModel)]="smestaj.brojKreveta" placeholder="Unesite broj kreveta">
      </div>
      <div class="field">
        <label>Cena Po Noći</label>
        <input type="number" [(ngModel)]="smestaj.cenaPoNoci" placeholder="Unesite cenu po noći">
      </div>
      <button class="ui teal button" (click)="dodajSobu()">Dodaj Sobu</button>
    </div>
  </div>
</div>

</body>
</html>
